      out.write("                var matchStartDate = true;\n");
      out.write("                var matchCompletionDate = true;\n");
      out.write("                \n");
      out.write("                // Filter by start date\n");
      out.write("                if(startDate) {\n");
      out.write("                    if(task.startDate) {\n");
      out.write("                        var taskStartDate = new Date(task.startDate);\n");
      out.write("                        var filterStartDate = new Date(startDate);\n");
